const utils = require("../../utils");
const erase = async (parser, predicate) => {
    const deletedData = [];
    const deleteData = data => {
        if (utils.isSubsetOf(predicate, data)) {
            deletedData.push(data);
        }
        else {
            return data;
        }
    };
    const events = {
        onEdit: deleteData
    };
    await parser.edit(events);
    return deletedData;
};
module.exports = erase;
